        public static void sp_EmpMonitoring (SqlConnection con1)
        {
            con1.Close();
            con1.Open();
            string sql = "-- =============================================" + Environment.NewLine +
                         "-- Author:		Bibhas Chakraborty" + Environment.NewLine +
                         "-- Create date: 15/08/2021" + Environment.NewLine +
                         "-- Description:	Employee Monitoring Month and Company wise" + Environment.NewLine +
                         "-- =============================================" + Environment.NewLine +
                         "CREATE PROCEDURE sp_EmpMonitoring " + Environment.NewLine +
                            "@curMon nvarchar(Max)," + Environment.NewLine +
                            "@coid int," + Environment.NewLine +
                            "@dur int" + Environment.NewLine +
                         "AS" + Environment.NewLine +
                         "BEGIN" + Environment.NewLine +

                         "if (@coid='0')" + Environment.NewLine +
                              "begin" + Environment.NewLine +

                         "select ID,Code,Name,LPM as lsal_mon,CAST('15-'+ @curMon AS Date )status_date,(DATENAME ( month , [LPM] ) +'-'+CAST(YEAR([LPM]) AS CHAR(4)))  as [Last Paid Month]," +
                         "[Current Month],[Duration],(case when [Duration]>=@dur then 'Dormant' else 'Active' end) 'Status',(case when active=1 then 'Active' else 'InActive' end)Active,locid,company_id from (select distinct ID as ID, " +
                         "(select distinct ((CASE WHEN ltrim(rtrim(em.FirstName)) != '' THEN em.FirstName + ' ' ELSE '' END) + (CASE WHEN ltrim(rtrim(em.MiddleName)) != '' THEN em.MiddleName + ' ' ELSE '' END) + (CASE WHEN ltrim(rtrim(em.LastName)) != '' THEN em.LastName + ' ' ELSE '' END)) from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS 'Name'," +
                         "(select Code from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS Code," +
                         "(select active from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS active," +
                         "(select LOcation_ID from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS locid,ea.company_id," +
                         "cast(max(('15-'+ [mon])) as Date) as [LPM],@curMon as [Current Month]," +
                         "DATEDIFF(month, cast(max('15-'+ [mon]) as Date), CAST('15-'+ @curMon AS Date )) [Duration] FROM tbl_Employee_Attend ea where cast(('05-'+ ea.mon) as Date)< cast(('15-'+ @curMon) as Date) and (Company_id in (select distinct CO_CODE from company)) group by Id,Company_id)x  where code!='' order by Company_id,ID" + Environment.NewLine +

                          "select @curMon as [Current Month],(select EmpLimit from CompanyLimiter) [Limit]," +
"(Select COUNT(Code) from tbl_Employee_Mast em  where (DateOfJoining<=CAST('15-'+ @curMon AS Date )) ) as [TotalEmployee]," +
"(Select COUNT(*) from tbl_Employee_Mast em  where  (active=1) and (DateOfJoining<=CAST('15-'+ @curMon AS Date )) ) as [Active]," +
"(Select COUNT(*) from tbl_Employee_Mast em  where  (active=0) and (DateOfJoining<=CAST('15-'+ @curMon AS Date )) ) as [Inactive]," +
"(select count(*) from (select (DATEDIFF(month, cast(max('15-'+ [mon]) as Date), CAST('15-'+ @curMon AS Date ))) dr,(select Code from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS Code,ID FROM tbl_Employee_Attend ea where (cast(('05-'+ ea.mon) as Date)< cast(('15-'+ @curMon) as Date)) and (Company_id in (select distinct CO_CODE from company) ) group by id,Company_id)x where dr>=@dur and code!='') as 'Dormant'" + Environment.NewLine +


                              "end" + Environment.NewLine +
                         "else" + Environment.NewLine +
                             "begin" + Environment.NewLine +

                         "select ID,Code,Name,LPM as lsal_mon,CAST('15-'+ @curMon AS Date )status_date,(DATENAME ( month , [LPM] ) +'-'+CAST(YEAR([LPM]) AS CHAR(4)))  as [Last Paid Month]," +
                         "[Current Month],[Duration],(case when [Duration]>=@dur then 'Dormant' else 'Running' end) 'Status',(case when active=1 then 'Active' else 'InActive' end)Active,locid,company_id from (select distinct ID as ID, " +
                         "(select distinct ((CASE WHEN ltrim(rtrim(em.FirstName)) != '' THEN em.FirstName + ' ' ELSE '' END) + (CASE WHEN ltrim(rtrim(em.MiddleName)) != '' THEN em.MiddleName + ' ' ELSE '' END) + (CASE WHEN ltrim(rtrim(em.LastName)) != '' THEN em.LastName + ' ' ELSE '' END)) from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS 'Name'," +
                         "(select Code from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS Code," +
                         "(select active from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS active," +
                         "(select LOcation_ID from tbl_Employee_Mast em  where em.ID=ea.ID and em.Company_id=ea.Company_id) AS locid,ea.company_id," +
