    "@mod AS MOD, @mod AS Wday, 0 AS Absent, 0 AS Proxy, 0 AS ed,0 as lv_earn, 0 as lv_adj, ISNULL((select ISNULL(cur_lv_bal,0) from tbl_Emp_Leave_Balance b where (b.eid=ea.ID)),0) as 'lv_pbal'," + Environment.NewLine +
