        public static void DOCUMENTS_COPY(SqlConnection con)
        {
            string sqlstr = " if exists (select * from sysobjects where id = object_id('COPY_BILL')" + Environment.NewLine;
            sqlstr = sqlstr + " and OBJECTPROPERTY(id, 'IsProcedure') = 1)" + Environment.NewLine;
            sqlstr = sqlstr + " drop procedure COPY_BILL" + Environment.NewLine;
            SqlCommand cmd = new SqlCommand(sqlstr, con);
            try
            {
                cmd.ExecuteNonQuery();

                sqlstr = " CREATE PROCEDURE COPY_BILL @SourceFicode char(10),@SourceGcode char(10),@SourceT_Entry VARCHAR(10),@SourceVoucher int,@SourceBILLNO VARCHAR(30),@DestFicode Char(10),@DestGcode Char(10),@DestVoucher int,@DestPartyCode int" + Environment.NewLine;
                sqlstr = sqlstr + " AS BEGIN" + Environment.NewLine;
                sqlstr = sqlstr + " INSERT INTO BILL(FICODE,GCODE,T_ENTRY,VOUCHER,BILLNO,BILLDATE,PARTYCODE,DB_CR,DUEDATE,BILLAMT," + Environment.NewLine;
                sqlstr = sqlstr + " BALAMT,CFORMNO,CFORMNAME,SERVICE,REF_TENTRY,REF_VOUCHER,REF_USER_VCH,CURR_CODE,BRNCH_CODE,NCONV_STR," + Environment.NewLine;
