                sqlstr = sqlstr + " FROM Price_List_Master WHERE FICODE=@SourceFicode AND GCODE=@SourceGcode" + Environment.NewLine;

                sqlstr = sqlstr + " End" + Environment.NewLine;
                con.Close();
                con.Open();
                cmd = new SqlCommand(sqlstr, con);
                try
                {
                    cmd.ExecuteNonQuery();
                    //con.Close();
                }
                catch { }
            }
            catch { }
        }

